import { Component, OnInit } from '@angular/core';

declare var google: any;

@Component({
  selector: 'app-gas-calculator',
  templateUrl: './gas-calculator.component.html',
  styleUrls: ['./gas-calculator.component.css']
})
export class GasCalculatorComponent implements OnInit {
  directionsService: any;
  startInput: any;
  endInput: any;
  fuelCapacityInput: any;
  mpg: any;
  tankCapacity: any;
  distance: any;
  fuelCapacity: any;

  constructor() {
    this.directionsService = new google.maps.DirectionsService();
  }

  ngOnInit(): void {
    this.startInput = document.getElementById('startLocation');
    this.endInput = document.getElementById('endLocation');
    this.fuelCapacityInput = document.getElementById('fuelCapacity');

    const startAutocomplete = new google.maps.places.Autocomplete(this.startInput);
    const endAutocomplete = new google.maps.places.Autocomplete(this.endInput);

    this.mpg = (document.getElementById('mpg') as HTMLInputElement)?.value;
    this.tankCapacity = (document.getElementById('tankCapacity') as HTMLInputElement)?.value;

    document.getElementById('mpg')?.addEventListener('change', () => {
      this.mpg = (document.getElementById('mpg') as HTMLInputElement)?.value;
      this.updateFuelCapacity();
    });

    document.getElementById('tankCapacity')?.addEventListener('change', () => {
      this.tankCapacity = (document.getElementById('tankCapacity') as HTMLInputElement)?.value;
      this.updateFuelCapacity();
    });
  }

  updateFuelCapacity() {
    const fuelCapacity = this.mpg * this.tankCapacity;
    this.fuelCapacityInput.value = fuelCapacity.toFixed(2);
  }

  calculateFuelAndDistance() {
    const startLocation = (document.getElementById('startLocation') as HTMLInputElement).value;
    const endLocation = (document.getElementById('endLocation') as HTMLInputElement).value;
    this.mpg = (document.getElementById('mpg') as HTMLInputElement)?.value;
    this.tankCapacity = (document.getElementById('tankCapacity') as HTMLInputElement)?.value;
    this.fuelCapacityInput = document.getElementById('fuelCapacity');
    const distanceInput = document.getElementById('distance');
    const stopsRequiredOutput = document.getElementById('stopsRequired');

    if (startLocation !== '' && endLocation !== '' && this.mpg !== '' && this.tankCapacity !== '') {
      this.directionsService.route(
        {
          origin: startLocation,
          destination: endLocation,
          travelMode: google.maps.TravelMode.DRIVING,
        },
        (response: any, status: any) => {
          if (status === google.maps.DirectionsStatus.OK) {
            const distance = response.routes[0].legs[0].distance.value / 1609.34;
            const distanceInput = document.getElementById('distance') as HTMLInputElement;
            if (distanceInput) {
              distanceInput.value = distance.toFixed(2);
            }

            const fuelCapacity = parseFloat(this.mpg) * parseFloat(this.tankCapacity);
            this.fuelCapacityInput.value = fuelCapacity.toFixed(2);

            const stopsRequired = Math.ceil(distance / fuelCapacity);
            const stopsRequiredOutput = document.getElementById('stopsRequired');
            stopsRequiredOutput!.innerHTML = `You will need to make ${stopsRequired ?? 0} stop(s) for gas.`;

          } else {
            const distanceInput = document.getElementById('distance') as HTMLInputElement;
            this.fuelCapacityInput.value = '';
            stopsRequiredOutput!.innerHTML = '';
            alert(`Directions request failed due to ${status}`);
          }
        }
      );
    } else {
      const distanceInput = document.getElementById('distance') as HTMLInputElement;
      this.fuelCapacityInput.value = '';
      stopsRequiredOutput!.innerHTML = '';
    }
  }

  onInputChange() {
    this.calculateFuelAndDistance();
  }
}
<!DOCTYPE html>
<html>
<head>
  <title>Fuel Calculator</title>
  <style>
    label {
      display: block;
      margin-bottom: 10px;
    }
    input[type=text] {
      padding: 5px;
      width: 100%;
      box-sizing: border-box;
      margin-bottom: 10px;
    }
    button {
      padding: 10px 20px;
      background-color: #4CAF50;
      color: white;
      border: none;
      border-radius: 4px;
      cursor: pointer;
      font-size: 16px;
    }
    button:hover {
      background-color: #45a049;
    }
    #output {
      margin-top: 20px;
    }
  </style>

</head>
<body>
<div>
  <div>
    <label for="startLocation">Start Location:</label>
    <input type="text" id="startLocation" name="startLocation" [(ngModel)]="startLocation" (input)="onInputChange()" />

    <label for="endLocation">End Location:</label>
    <input type="text" id="endLocation" name="endLocation" [(ngModel)]="endLocation" (input)="onInputChange()" />

    <label for="mpg">MPG:</label>
    <input type="number" id="mpg" name="mpg" [(ngModel)]="mpg" (change)="onInputChange()" />

    <label for="tankCapacity">Tank Capacity:</label>
    <input type="number" id="tankCapacity" name="tankCapacity" [(ngModel)]="tankCapacity" (change)="onInputChange()" />

    <label for="fuelCapacity">Fuel Capacity (Miles):</label>
    <input type="text" id="fuelCapacity" name="fuelCapacity" readonly [(ngModel)]="fuelCapacity" />

    <label for="distance">Distance (Miles):</label>
    <input type="text" id="distance" name="distance" readonly [(ngModel)]="distance" />

    <p id="stopsRequired"></p>

    <button type="button" (click)="calculateFuelAndDistance()">Calculate</button>

  </div>


</div>


<script src="https://maps.googleapis.com/maps/api/js?key=AIzaSyAveULz0kjUIAZ--Ufdo0n7wBswHdo_y8o&callback=initMap&libraries=places" defer></script>
<script src="script.js"></script>
</body>
</html>
