import { Component } from '@angular/core';
import { AuthService } from '../services/auth.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {
  constructor(public authService: AuthService) { }

  logout() {
    this.authService.logout();
  }
}
<header>
  <div class="logo">
    <a routerLink="/home" aria-label="Homepage">
      <img src="https://th.bing.com/th/id/R.ff05c4db9f34a2b0dc7a3d5305bbd431?rik=Wo1iZjI%2bGz6XUg&riu=http%3a%2f%2fpluspng.com%2fimg-png%2fglobe-hd-png-globe-clip-art-free-download-1906.png&ehk=kZHEITLFptqimIQfJ6OfR3hsB0J%2flJ2v3FYPGS6hPgI%3d&risl=&pid=ImgRaw&r=0" alt="Along The Way">
    </a>
    <a href="/"><img src="https://i.imgur.com/1mJ9Art.png"/></a>
  </div>
  <nav>
    <ul>
      <!-- Only show these options if the user is logged in -->
      <li *ngIf="isLoggedIn"><a href="/saved-trips">Saved Trips</a></li>
      <li *ngIf="isLoggedIn"><a href="/profile">My Profile</a></li>
      <li *ngIf="isLoggedIn"><a (click)="logout()">Logout</a></li>

      <!-- Only show these options if the user is not logged in -->
      <li *ngIf="!isLoggedIn"><a routerLink="/login">Login</a></li>
      <li *ngIf="!isLoggedIn"><a routerLink="/signup">Sign Up</a></li>
      
      <li><a routerLink="/about">About Us/FAQ</a></li>
    </ul>
  </nav>
</header>
