import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { tap } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private isLoggedIn$ = new BehaviorSubject<boolean>(false);

  get isLoggedIn(): Observable<boolean> {
    return this.isLoggedIn$.asObservable();
  }

  constructor(private http: HttpClient) {}

  login(username: string, password: string): Observable<any> {
    const url = 'http://localhost:8080/auth/login'; // Replace with the actual backend URL
    const loginData = {
      username: username,
      password: password
    };
    return this.http.post(url, loginData).pipe(
      tap(response => {
        const token = response; // Assign the entire response object to token
        this.isLoggedIn$.next(!!token);
      })
    );
  }


  logout(): Observable<any> {
    // Make an HTTP POST request to the logout endpoint
    return this.http.post<any>('/auth/logout', {}).pipe(
      tap(() => {
        // Update isLoggedIn$ to false
        this.isLoggedIn$.next(false);
      })
    );
  }
}
