import { Component, OnInit } from '@angular/core';
import { AuthService } from '../services/auth.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit {
  username: string | null = null;

  constructor(public authService: AuthService) { }

  ngOnInit(): void {
    this.authService.getUsername.subscribe(username => {
      this.username = username;
    });
  }

  logout(): void {
    this.authService.logout();
  }
}
<header>
  <div class="logo">
    <a routerLink="/home" aria-label="Homepage">
      <img src="https://th.bing.com/th/id/R.ff05c4db9f34a2b0dc7a3d5305bbd431?rik=Wo1iZjI%2bGz6XUg&riu=http%3a%2f%2fpluspng.com%2fimg-png%2fglobe-hd-png-globe-clip-art-free-download-1906.png&ehk=kZHEITLFptqimIQfJ6OfR3hsB0J%2flJ2v3FYPGS6hPgI%3d&risl=&pid=ImgRaw&r=0" alt="Along The Way">
    </a>
    <a href="/"><img src="https://i.imgur.com/1mJ9Art.png"/></a>
  </div>
  <nav>
    <ul>
      <li *ngIf="(authService.isLoggedIn | async) as isLoggedIn">
        <a href="/saved-trips" *ngIf="isLoggedIn">Saved Trips</a>
        <a href="/profile" *ngIf="isLoggedIn">My Profile</a>
        <a (click)="logout()" *ngIf="isLoggedIn">Logout</a>
      </li>
      <li *ngIf="!(authService.isLoggedIn | async)">
        <a routerLink="/login">Login</a>
      </li>
      <li *ngIf="!(authService.isLoggedIn | async)">
        <a routerLink="/signup">Sign Up</a>
      </li>
      <li><a routerLink="/about">About Us/FAQ</a></li>
    </ul>
    <span *ngIf="authService.isLoggedIn | async">Hi {{ username }}!</span>
  </nav>
</header>
