import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Router } from '@angular/router';
import { BehaviorSubject, Observable } from 'rxjs';
import { tap } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private isLoggedIn$ = new BehaviorSubject<boolean>(false);

  get isLoggedIn(): Observable<boolean> {
    return this.isLoggedIn$.asObservable();
  }

  constructor(private http: HttpClient, private router: Router) {
    // Check if token exists in localStorage
    const token = localStorage.getItem('token');
    this.isLoggedIn$.next(!!token);
  }

  login(username: string, password: string): Observable<any> {
    const url = 'http://localhost:8080/auth/login'; // Replace with the actual backend URL
    const loginData = {
      username: username,
      password: password
    };
    return this.http.post(url, loginData).pipe(
      tap(response => {
        const token = (response as any).token as string; // Use type assertion to access the 'token' property
        localStorage.setItem('token', token); // Save token to localStorage
        this.isLoggedIn$.next(!!token);
        if (!!token) {
          // Redirect to the desired page
          this.router.navigate(['/dashboard']); // Replace '/dashboard' with the desired URL
        }
      })
    );
  }





  logout(): Observable<any> {
    // Remove token from localStorage
    localStorage.removeItem('token');

    // Update isLoggedIn$ to false
    this.isLoggedIn$.next(false);

    // Make an HTTP POST request to the logout endpoint
    return this.http.post<any>('/auth/logout', {});
  }

}
