import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit {

  constructor() { }

  ngOnInit(): void {
  }

}
<header>
  <div class="logo">
    <a routerLink="/home" aria-label="Homepage">
      <img src="assets/images/logo.png" alt="Along The Way">
    </a>
    <a href="/" class="nav-button"><img src="assets/images/logo-text.png" /></a>
  </div>
  <nav>
    <ul>
      <li><a routerLink="/about" class="nav-button">ABOUT US</a></li>
      <li><a href="/faq" class="nav-button">FAQ</a></li>
      <li><a href="/profile" class="nav-button">PROFILE</a></li>
    </ul>
  </nav>
</header>
